package VariousLoops;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		forloop obj=new forloop();
		obj.whileloop();
		
		
		
}
	
	
public void forloop(){
		
		for(int i=1;i<=10;i++)
			
		{
			
			System.out.println(i);
		}
		
		}


public void whileloop() 
{
	int i=0;
	
	while(i<=10)
	{
		
		System.out.println(i);
		i=i+2;
		
		
	}





}

}
